import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  LayoutDashboard, 
  Database,
  Code,
  Package,
  Users,
  Shield,
  Settings,
  Box,
  LogOut
} from 'lucide-react';

const Layout = ({ children }) => {
  const location = useLocation();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    navigate('/login', { replace: true });
  };

  // Sidebar menu items
  const menuItems = [
    { icon: LayoutDashboard, text: 'Overview', path: '/dashboard' },
    { icon: Box, text: 'Components', path: '/components' },
    { icon: Code, text: 'APIs', path: '/apis' },
    { icon: Database, text: 'Resources', path: '/resources' },
    { icon: Package, text: 'Systems', path: '/systems' },
    { icon: Users, text: 'Teams', path: '/teams' },
    { icon: Shield, text: 'Security', path: '/security' },
    { icon: Settings, text: 'Settings', path: '/settings' }
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Left Sidebar */}
      <div className="w-64 bg-white border-r border-gray-200 fixed h-full">
        {/* Logo */}
        <div className="p-6">
          <h1 className="text-xl font-bold text-gray-900">OpsCanvas</h1>
        </div>

        {/* Navigation Menu */}
        <nav className="mt-4">
          {menuItems.map((item, index) => (
            <Link
              key={index}
              to={item.path}
              className={`flex items-center px-6 py-2 text-sm font-medium ${
                location.pathname === item.path
                  ? 'text-blue-600 bg-blue-50'
                  : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
              }`}
            >
              <item.icon className="w-5 h-5 mr-3" />
              {item.text}
            </Link>
          ))}
        </nav>

        {/* Logout Button */}
        <button
          onClick={handleLogout}
          className="absolute bottom-0 w-full p-4 flex items-center text-gray-600 hover:text-gray-900 hover:bg-gray-50"
        >
          <LogOut className="w-5 h-5 mr-3" />
          <span>Logout</span>
        </button>
      </div>

      {/* Main Content */}
      <div className="ml-64 flex-1">
        {/* Top Navigation */}
        <div className="bg-white border-b border-gray-200">
          <div className="flex justify-between items-center px-8 py-4">
            <div className="flex space-x-6">
              <Link
                to="/dashboard"
                className={`px-3 py-2 text-sm font-medium ${
                  location.pathname === '/dashboard'
                    ? 'text-blue-600 border-b-2 border-blue-600'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                Dashboard
              </Link>
              <Link
                to="/resources"
                className={`px-3 py-2 text-sm font-medium ${
                  location.pathname === '/resources'
                    ? 'text-blue-600 border-b-2 border-blue-600'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                Resources
              </Link>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">Admin</span>
              <button 
                onClick={handleLogout}
                className="px-3 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700"
              >
                Logout
              </button>
            </div>
          </div>
        </div>

        {/* Page Content */}
        <div className="p-8">
          {children}
        </div>
      </div>
    </div>
  );
};

export default Layout;
