import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import Dashboard from './pages/Dashboard';
import Resources from './pages/Resources';
import ServiceCatalog from './pages/ServiceCatalog';
import Layout from './components/Layout';
import Documentation from './pages/Documentation';
import CICDDashboard from './components/pipelines/CICDDashboard';

const ProtectedRoute = ({ children }) => {
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
  
  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return <Layout>{children}</Layout>;
};

const PublicRoute = ({ children }) => {
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
  
  if (isAuthenticated) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

const App = () => {
  return (
    <Routes>
      <Route 
        path="/login" 
        element={
          <PublicRoute>
            <LoginPage />
          </PublicRoute>
        } 
      />
      <Route 
        path="/dashboard" 
        element={
          <ProtectedRoute>
            <Dashboard />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/resources/*" 
        element={
          <ProtectedRoute>
            <Resources />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/catalog/*" 
        element={
          <ProtectedRoute>
            <ServiceCatalog />
          </ProtectedRoute>
        } 
      />
      <Route
        path="/docs"
        element={
          <ProtectedRoute>
            <Documentation />
          </ProtectedRoute>
        }
      />
      {/* New Pipeline Routes */}
      <Route
        path="/pipelines"
        element={
          <ProtectedRoute>
            <CICDDashboard />
          </ProtectedRoute>
        }
      />
      <Route
        path="/pipelines/history"
        element={
          <ProtectedRoute>
            <CICDDashboard view="history" />
          </ProtectedRoute>
        }
      />
      <Route
        path="/pipelines/environments"
        element={
          <ProtectedRoute>
            <CICDDashboard view="environments" />
          </ProtectedRoute>
        }
      />
      <Route path="/" element={<Navigate to="/dashboard" replace />} />
    </Routes>
  );
};

export default App;
