// src/components/docker/DockerManagement.jsx
import React, { useState } from 'react';
import { Box, RefreshCw, Shield, Search } from 'lucide-react';
import SecurityScanner from './SecurityScanner';
import ContainerList from './ContainerList';
import ImageList from './ImageList';
import RegistryManager from './RegistryManager';

const DockerManagement = () => {
  const [activeTab, setActiveTab] = useState('containers');
  const [searchTerm, setSearchTerm] = useState('');

  const tabs = [
    { id: 'containers', label: 'Containers', icon: Box },
    { id: 'images', label: 'Images', icon: Box },
    { id: 'security', label: 'Security', icon: Shield },
    { id: 'registry', label: 'Registry', icon: Box }
  ];

  const renderContent = () => {
    switch (activeTab) {
      case 'containers':
        return <ContainerList searchTerm={searchTerm} />;
      case 'images':
        return <ImageList searchTerm={searchTerm} />;
      case 'security':
        return <SecurityScanner />;
      case 'registry':
        return <RegistryManager />;
      default:
        return null;
    }
  };

  return (
    <div className="p-6">
      {/* Header with Search */}
      <div className="mb-6 flex justify-between items-center">
        <h2 className="text-lg font-semibold text-gray-900">Docker Management</h2>
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search..."
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200 mb-6">
        <nav className="-mb-px flex space-x-8">
          {tabs.map(tab => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`group inline-flex items-center py-4 px-1 border-b-2 font-medium text-sm
                  ${activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
              >
                <Icon className={`w-5 h-5 mr-2 ${
                  activeTab === tab.id ? 'text-blue-500' : 'text-gray-400 group-hover:text-gray-500'
                }`} />
                {tab.label}
              </button>
            );
          })}
        </nav>
      </div>

      {/* Main Content */}
      {renderContent()}
    </div>
  );
};

export default DockerManagement;
