// src/components/Layout.jsx
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  LayoutDashboard, 
  Database,
  Code,
  Package,
  Users,
  Shield,
  Settings,
  Server,
  Book,
  ChevronDown,
  ChevronUp,
  GitBranch,
  Activity,
  Cloud,
  Box,
  Container,
  Image
} from 'lucide-react';
import Header from './header/Header';
import OceanWaveLogo from './OceanWaveLogo';

const Layout = ({ children }) => {
  const location = useLocation();
  const [expandedSection, setExpandedSection] = useState(null);

  // Helper functions
  const isPathActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + '/');
  };

  const getEnvironmentStatus = () => {
    const environments = {
      production: { color: 'bg-green-500', text: 'Production Environment' },
      staging: { color: 'bg-yellow-500', text: 'Staging Environment' },
      development: { color: 'bg-blue-500', text: 'Development Environment' }
    };
    return environments.production; // You can modify this logic based on your needs
  };

  const getCurrentPageTitle = () => {
    const currentPath = location.pathname;
    const currentMenuItem = menuItems.find(item => {
      if (item.path === currentPath) return true;
      if (item.subItems) {
        return item.subItems.some(subItem => subItem.path === currentPath);
      }
      return false;
    });
    return currentMenuItem ? currentMenuItem.text : 'Dashboard';
  };

  const getCurrentPageDescription = () => {
    switch (location.pathname) {
      case '/dashboard':
        return 'Monitor and manage your platform resources';
      case '/resources':
        return 'Manage and monitor your infrastructure resources';
      case '/catalog':
        return 'Browse and manage all services and applications';
      case '/docs':
        return 'Access and manage platform documentation';
      case '/pipelines':
        return 'Monitor and manage CI/CD pipelines and deployments';
      case '/pipelines/history':
        return 'View build and deployment history';
      case '/pipelines/environments':
        return 'Manage deployment environments';
      case '/kubernetes':
      case '/kubernetes/clusters':
        return 'Manage and monitor Kubernetes clusters';
      case '/kubernetes/namespaces':
        return 'Configure and manage Kubernetes namespaces';
      case '/kubernetes/workloads':
        return 'Monitor and manage Kubernetes workloads';
      case '/kubernetes/helm':
        return 'Manage Helm charts and releases';
      case '/kubernetes/rbac':
        return 'Configure Kubernetes role-based access control';
      case '/docker':
      case '/docker/containers':
        return 'Manage and monitor Docker containers';
      case '/docker/images':
        return 'Manage Docker images and repositories';
      case '/docker/security':
        return 'Monitor container security and vulnerabilities';
      case '/docker/registry':
        return 'Manage container registries and image distribution';
      default:
        return 'Manage your platform components and services';
    }
  };

  const currentEnv = getEnvironmentStatus();

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Enhanced Left Sidebar */}
      <div className="w-sidebar fixed h-full bg-sidebar-gradient shadow-sidebar overflow-y-auto">
        {/* Enhanced Logo Area */}
        <div className="p-6 border-b border-sidebar-border flex items-center">
          <OceanWaveLogo className="h-8 w-8 mr-3 text-brand-accent" />
          <h1 className="text-xl font-bold text-white">OpsCanvas</h1>
        </div>

        {/* Enhanced Navigation Menu */}
        <nav className="mt-4 flex flex-col">
          {menuItems.map((item, index) => (
            <div key={index} className="flex flex-col">
              <button
                onClick={() => item.subItems && setExpandedSection(expandedSection === index ? null : index)}
                className={`
                  flex items-center justify-between px-6 py-3 text-sm font-medium
                  transition-all duration-200 ease-in-out
                  group hover:bg-menu-item-hover
                  ${isPathActive(item.path)
                    ? `text-menu-${item.text.toLowerCase()} bg-sidebar-active`
                    : 'text-sidebar-text hover:text-white'
                  }
                `}
              >
                <div className="flex items-center">
                  <item.icon 
                    className={`
                      w-5 h-5 mr-3 transition-colors duration-200
                      ${isPathActive(item.path)
                        ? `text-menu-${item.text.toLowerCase()}`
                        : 'text-sidebar-text group-hover:text-white'
                      }
                    `} 
                  />
                  <span className="animate-sidebar-item">{item.text}</span>
                </div>
                {item.subItems && (
                  <ChevronDown 
                    className={`
                      w-4 h-4 transition-transform duration-200
                      ${expandedSection === index ? 'rotate-180' : ''}
                      ${isPathActive(item.path) ? `text-menu-${item.text.toLowerCase()}` : 'text-sidebar-text'}
                    `}
                  />
                )}
              </button>
              
              {/* Enhanced Submenu Items */}
              {item.subItems && expandedSection === index && (
                <div className="bg-sidebar-hover pl-14 py-2 animate-fadeIn">
                  {item.subItems.map((subItem, subIndex) => (
                    <Link
                      key={subIndex}
                      to={subItem.path}
                      className={`
                        flex items-center py-2 text-sm
                        transition-colors duration-150
                        group hover:text-white
                        ${location.pathname === subItem.path
                          ? `text-menu-${item.text.toLowerCase()}`
                          : 'text-sidebar-text'
                        }
                      `}
                    >
                      {subItem.icon && 
                        <subItem.icon 
                          className={`
                            w-4 h-4 mr-2
                            ${location.pathname === subItem.path
                              ? `text-menu-${item.text.toLowerCase()}`
                              : 'text-sidebar-text group-hover:text-white'
                            }
                          `}
                        />
                      }
                      <span className="animate-sidebar-item">{subItem.text}</span>
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </nav>

        {/* Enhanced Environment Indicator */}
        <div className="absolute bottom-0 w-full p-4 border-t border-sidebar-border bg-sidebar-hover">
          <div className="flex items-center space-x-2">
            <div className={`w-2 h-2 rounded-full animate-pulse ${currentEnv.color}`}></div>
            <span className="text-sm text-sidebar-text">{currentEnv.text}</span>
          </div>
        </div>
      </div>

      {/* Enhanced Main Content Area */}
      <div className="ml-sidebar flex-1 flex flex-col">
        {/* Header Component */}
        <Header currentPage={getCurrentPageTitle()} />

        {/* Enhanced Page Content */}
        <main className="flex-1 p-8 bg-gray-50">
          {/* Enhanced Page Title */}
          <div className="mb-8 animate-slideIn">
            <h1 className="text-2xl font-bold text-gray-900">{getCurrentPageTitle()}</h1>
            <p className="mt-1 text-sm text-gray-500">
              {getCurrentPageDescription()}
            </p>
          </div>

          {/* Enhanced Main Content */}
          <div className="bg-white rounded-lg shadow-lg p-6 animate-fadeIn">
            {children}
          </div>
        </main>

        {/* Enhanced Footer */}
        <footer className="bg-white border-t border-gray-200 p-4">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center text-sm text-gray-500">
              <p>© 2024 OpsCanvas. All rights reserved.</p>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Layout;
