import React, { useState, useEffect } from 'react';
import { 
  GitBranch, 
  PlayCircle, 
  CheckCircle2, 
  XCircle,
  Clock,
  AlertCircle,
  ArrowUpCircle
} from 'lucide-react';
import PipelineStats from './PipelineStats';
import DeploymentHistory from './DeploymentHistory';
import EnvironmentStatus from './EnvironmentStatus';
import BuildMetrics from './BuildMetrics';


const CICDDashboard = () => {
  const [pipelineData, setPipelineData] = useState({
    recentDeployments: [],
    environments: {},
    metrics: {}
  });

  useEffect(() => {
    // Simulated real-time data - replace with actual API calls
    const mockData = {
      recentDeployments: [
        {
          id: 1,
          service: 'User Authentication Service',
          status: 'success',
          timestamp: new Date().toISOString(),
          environment: 'production',
          commit: 'feat: Add OAuth integration',
          branch: 'main',
          duration: '5m 30s',
          triggeredBy: 'John Doe'
        },
        {
          id: 2,
          status: 'running',
          service: 'Payment Gateway',
          timestamp: new Date(Date.now() - 1000 * 60 * 10).toISOString(),
          environment: 'staging',
          commit: 'fix: Handle timeout errors',
          branch: 'develop',
          duration: '3m 45s',
          triggeredBy: 'Jane Smith'
        },
        // Add more mock deployments
      ],
      environments: {
        production: {
          status: 'healthy',
          lastDeployment: new Date().toISOString(),
          uptime: '99.99%',
          services: 12,
          activeIncidents: 0
        },
        staging: {
          status: 'deploying',
          lastDeployment: new Date(Date.now() - 1000 * 60 * 30).toISOString(),
          uptime: '99.95%',
          services: 12,
          activeIncidents: 1
        },
        development: {
          status: 'healthy',
          lastDeployment: new Date(Date.now() - 1000 * 60 * 60).toISOString(),
          uptime: '99.90%',
          services: 15,
          activeIncidents: 2
        }
      },
      metrics: {
        deploymentFrequency: {
          daily: 12,
          weekly: 85,
          monthly: 342
        },
        successRate: {
          daily: 95.5,
          weekly: 94.8,
          monthly: 96.2
        },
        averageDuration: {
          daily: '4m 30s',
          weekly: '4m 45s',
          monthly: '4m 15s'
        },
        failureRate: {
          daily: 4.5,
          weekly: 5.2,
          monthly: 3.8
        }
      }
    };

    setPipelineData(mockData);

    // Simulate real-time updates
    const interval = setInterval(() => {
      setPipelineData(prev => ({
        ...prev,
        recentDeployments: [
          {
            id: Date.now(),
            service: 'Random Service',
            status: Math.random() > 0.8 ? 'failed' : 'success',
            timestamp: new Date().toISOString(),
            environment: ['production', 'staging', 'development'][Math.floor(Math.random() * 3)],
            commit: 'Update: ' + Date.now(),
            branch: Math.random() > 0.5 ? 'main' : 'develop',
            duration: Math.floor(Math.random() * 10) + 'm ' + Math.floor(Math.random() * 60) + 's',
            triggeredBy: ['John Doe', 'Jane Smith'][Math.floor(Math.random() * 2)]
          },
          ...prev.recentDeployments.slice(0, 9)
        ]
      }));
    }, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="space-y-6">
      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Deployments Today</p>
              <p className="text-2xl font-semibold text-gray-900">{pipelineData.metrics?.deploymentFrequency?.daily || 0}</p>
            </div>
            <Play className="w-8 h-8 text-blue-500" />
          </div>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Success Rate (24h)</p>
              <p className="text-2xl font-semibold text-gray-900">{pipelineData.metrics?.successRate?.daily || 0}%</p>
            </div>
            <CheckCircle2 className="w-8 h-8 text-green-500" />
          </div>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Avg Deployment Time</p>
              <p className="text-2xl font-semibold text-gray-900">{pipelineData.metrics?.averageDuration?.daily || '0m'}</p>
            </div>
            <Clock className="w-8 h-8 text-orange-500" />
          </div>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Failed Deployments</p>
              <p className="text-2xl font-semibold text-gray-900">{pipelineData.metrics?.failureRate?.daily || 0}%</p>
            </div>
            <XCircle className="w-8 h-8 text-red-500" />
          </div>
        </div>
      </div>

      {/* Environment Status */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Environment Status</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {Object.entries(pipelineData.environments || {}).map(([env, data]) => (
              <EnvironmentStatus key={env} environment={env} data={data} />
            ))}
          </div>
        </div>
      </div>

      {/* Recent Deployments */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Recent Deployments</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full">
              <thead>
                <tr className="border-b border-gray-200">
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Status</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Service</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Environment</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Branch</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Duration</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Triggered By</th>
                  <th className="py-3 px-4 text-left text-sm font-medium text-gray-500">Time</th>
                </tr>
              </thead>
              <tbody>
                {pipelineData.recentDeployments.map((deployment) => (
                  <tr key={deployment.id} className="border-b border-gray-100">
                    <td className="py-3 px-4">
                      <PipelineStatus status={deployment.status} />
                    </td>
                    <td className="py-3 px-4 text-sm text-gray-900">{deployment.service}</td>
                    <td className="py-3 px-4 text-sm text-gray-900 capitalize">{deployment.environment}</td>
                    <td className="py-3 px-4 text-sm text-gray-900">
                      <div className="flex items-center">
                        <GitBranch className="w-4 h-4 mr-1 text-gray-500" />
                        {deployment.branch}
                      </div>
                    </td>
                    <td className="py-3 px-4 text-sm text-gray-900">{deployment.duration}</td>
                    <td className="py-3 px-4 text-sm text-gray-900">{deployment.triggeredBy}</td>
                    <td className="py-3 px-4 text-sm text-gray-500">
                      {new Date(deployment.timestamp).toLocaleTimeString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Deployment Metrics Chart */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Deployment Metrics</h2>
        <PipelineMetrics metrics={pipelineData.metrics} />
      </div>
    </div>
  );
};

export default CICDDashboard;
