// src/components/kubernetes/NamespaceManager.jsx
import React, { useState } from 'react';
import { 
  Plus, 
  Edit2, 
  Trash2, 
  AlertCircle, 
  CheckCircle,
  RefreshCw
} from 'lucide-react';

const NamespaceManager = () => {
  const [namespaces, setNamespaces] = useState([
    {
      name: 'default',
      status: 'active',
      pods: 12,
      resourceQuota: {
        cpu: { used: '2.5', limit: '4' },
        memory: { used: '4.2', limit: '8' },
        pods: { used: '12', limit: '20' }
      },
      created: '2024-02-15T10:00:00Z',
      labels: { environment: 'production', team: 'platform' }
    },
    {
      name: 'monitoring',
      status: 'active',
      pods: 8,
      resourceQuota: {
        cpu: { used: '1.5', limit: '2' },
        memory: { used: '3.1', limit: '4' },
        pods: { used: '8', limit: '10' }
      },
      created: '2024-02-15T10:00:00Z',
      labels: { environment: 'production', team: 'devops' }
    }
  ]);

  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedNamespace, setSelectedNamespace] = useState(null);

  const getStatusColor = (status) => {
    switch (status) {
      case 'active':
        return 'text-green-600 bg-green-100';
      case 'terminating':
        return 'text-red-600 bg-red-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getResourceUsageColor = (used, limit) => {
    const percentage = (used / limit) * 100;
    if (percentage >= 90) return 'bg-red-500';
    if (percentage >= 70) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-lg font-semibold text-gray-900">Namespaces</h2>
        <button
          onClick={() => setShowCreateModal(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
        >
          <Plus className="w-4 h-4 mr-2" />
          Create Namespace
        </button>
      </div>

      {/* Namespaces Table */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Name
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Resource Usage
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Labels
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {namespaces.map((namespace) => (
              <tr key={namespace.name} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">
                    {namespace.name}
                  </div>
                  <div className="text-sm text-gray-500">
                    {new Date(namespace.created).toLocaleDateString()}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(namespace.status)}`}>
                    {namespace.status}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="space-y-2">
                    {/* CPU Usage */}
                    <div>
                      <div className="flex justify-between text-xs text-gray-500 mb-1">
                        <span>CPU</span>
                        <span>{namespace.resourceQuota.cpu.used}/{namespace.resourceQuota.cpu.limit} cores</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-1.5">
                        <div
                          className={`h-1.5 rounded-full ${getResourceUsageColor(
                            parseFloat(namespace.resourceQuota.cpu.used),
                            parseFloat(namespace.resourceQuota.cpu.limit)
                          )}`}
                          style={{
                            width: `${(parseFloat(namespace.resourceQuota.cpu.used) / parseFloat(namespace.resourceQuota.cpu.limit)) * 100}%`
                          }}
                        />
                      </div>
                    </div>
                    {/* Memory Usage */}
                    <div>
                      <div className="flex justify-between text-xs text-gray-500 mb-1">
                        <span>Memory</span>
                        <span>{namespace.resourceQuota.memory.used}/{namespace.resourceQuota.memory.limit} GB</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-1.5">
                        <div
                          className={`h-1.5 rounded-full ${getResourceUsageColor(
                            parseFloat(namespace.resourceQuota.memory.used),
                            parseFloat(namespace.resourceQuota.memory.limit)
                          )}`}
                          style={{
                            width: `${(parseFloat(namespace.resourceQuota.memory.used) / parseFloat(namespace.resourceQuota.memory.limit)) * 100}%`
                          }}
                        />
                      </div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex flex-wrap gap-2">
                    {Object.entries(namespace.labels).map(([key, value]) => (
                      <span
                        key={key}
                        className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                      >
                        {key}: {value}
                      </span>
                    ))}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  <div className="flex space-x-3">
                    <button className="text-blue-600 hover:text-blue-900">
                      <Edit2 className="w-4 h-4" />
                    </button>
                    <button className="text-red-600 hover:text-red-900">
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default NamespaceManager;
