// src/components/Layout.jsx
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  LayoutDashboard, 
  Database,
  Code,
  Package,
  Users,
  Shield,
  Settings,
  Server,
  Book,
  ChevronDown,
  ChevronUp,
  GitBranch,
  Activity,
  Cloud,
  Box,
  Container,
  Image
} from 'lucide-react';
import Header from './header/Header';
import OceanWaveLogo from './OceanWaveLogo';

const Layout = ({ children }) => {
  const location = useLocation();
  const [expandedSection, setExpandedSection] = useState(null);

  const menuItems = [
    { 
      icon: LayoutDashboard, 
      text: 'Overview', 
      path: '/dashboard',
      color: 'text-indigo-500'
    },
    { 
      icon: Server, 
      text: 'Service Catalog', 
      path: '/catalog',
      color: 'text-blue-500',
      subItems: [
        { text: 'Services List', path: '/catalog/services' },
        { text: 'Service Metrics', path: '/catalog/metrics' }
      ]
    },
    {
      icon: Cloud,
      text: 'Kubernetes',
      path: '/kubernetes',
      color: 'text-sky-500',
      subItems: [
        { text: 'Clusters', path: '/kubernetes/clusters', icon: Network },
        { text: 'Namespaces', path: '/kubernetes/namespaces', icon: Box },
        { text: 'Workloads', path: '/kubernetes/workloads', icon: Cpu },
        { text: 'Helm Charts', path: '/kubernetes/helm', icon: Package },
        { text: 'RBAC', path: '/kubernetes/rbac', icon: Shield }
      ]
    },
    {
      icon: Boxes,
      text: 'Docker',
      path: '/docker',
      color: 'text-cyan-500',
      subItems: [
        { text: 'Containers', path: '/docker/containers', icon: Container },
        { text: 'Images', path: '/docker/images', icon: Image },
        { text: 'Security', path: '/docker/security', icon: Shield },
        { text: 'Registry', path: '/docker/registry', icon: Database }
      ]
    },
    { 
      icon: Package, 
      text: 'Components', 
      path: '/components',
      color: 'text-teal-500',
      subItems: [
        { text: 'UI Components', path: '/components/ui' },
        { text: 'Backend Components', path: '/components/backend' },
        { text: 'Infrastructure', path: '/components/infrastructure' }
      ]
    },
    { 
      icon: Code, 
      text: 'APIs', 
      path: '/apis',
      color: 'text-emerald-500',
      subItems: [
        { text: 'API Documentation', path: '/apis/docs' },
        { text: 'API Testing', path: '/apis/testing' }
      ]
    },
    { 
      icon: Database, 
      text: 'Resources', 
      path: '/resources',
      color: 'text-green-500',
      subItems: [
        { text: 'Cloud Resources', path: '/resources/cloud' },
        { text: 'On-Premise', path: '/resources/onprem' }
      ]
    },
    {
      icon: GitBranch,
      text: 'CI/CD Pipelines',
      path: '/pipelines',
      color: 'text-lime-500',
      subItems: [
        { text: 'Pipeline Overview', path: '/pipelines' },
        { text: 'Build History', path: '/pipelines/history' },
        { text: 'Environments', path: '/pipelines/environments' }
      ]
    },
    { 
      icon: Cpu, 
      text: 'Systems', 
      path: '/systems',
      color: 'text-yellow-500',
      subItems: [
        { text: 'System Status', path: '/systems/status' },
        { text: 'System Metrics', path: '/systems/metrics' }
      ]
    },
    { 
      icon: Users, 
      text: 'Teams', 
      path: '/teams',
      color: 'text-orange-500'
    },
    { 
      icon: Shield, 
      text: 'Security', 
      path: '/security',
      color: 'text-red-500'
    },
    { 
      icon: FileText, 
      text: 'Documentation', 
      path: '/docs',
      color: 'text-pink-500'
    },
    { 
      icon: Settings, 
      text: 'Settings', 
      path: '/settings',
      color: 'text-purple-500'
    }
  ];

  // ... [previous functions remain the same] ...

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Left Sidebar */}
      <div className="w-64 bg-gradient-to-b from-gray-900 to-gray-800 fixed h-full overflow-y-auto">
        {/* Logo */}
        <div className="p-6 border-b border-gray-700 flex items-center">
          <OceanWaveLogo className="h-8 w-8 mr-3 text-blue-400" />
          <h1 className="text-xl font-bold text-white">OpsCanvas</h1>
        </div>

        {/* Navigation Menu */}
        <nav className="mt-4 flex flex-col">
          {menuItems.map((item, index) => (
            <div key={index} className="flex flex-col">
              <button
                onClick={() => item.subItems && setExpandedSection(expandedSection === index ? null : index)}
                className={`flex items-center justify-between px-6 py-3 text-sm font-medium transition-all duration-150 ${
                  isPathActive(item.path)
                    ? `${item.color} bg-gray-800/50`
                    : 'text-gray-300 hover:text-white hover:bg-gray-800/30'
                }`}
              >
                <div className="flex items-center">
                  <item.icon className={`w-5 h-5 mr-3 ${item.color}`} />
                  <span>{item.text}</span>
                </div>
                {item.subItems && (
                  expandedSection === index ? 
                    <ChevronUp className="w-4 h-4 text-gray-400" /> : 
                    <ChevronDown className="w-4 h-4 text-gray-400" />
                )}
              </button>
              
              {/* Submenu Items */}
              {item.subItems && expandedSection === index && (
                <div className="bg-gray-800/30 pl-14 py-2">
                  {item.subItems.map((subItem, subIndex) => (
                    <Link
                      key={subIndex}
                      to={subItem.path}
                      className={`flex items-center py-2 text-sm transition-colors duration-150 ${
                        location.pathname === subItem.path
                          ? `${item.color}`
                          : 'text-gray-400 hover:text-white'
                      }`}
                    >
                      {subItem.icon && <subItem.icon className="w-4 h-4 mr-2" />}
                      {subItem.text}
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </nav>

        {/* Environment Indicator */}
        <div className="absolute bottom-0 w-full p-4 border-t border-gray-700 bg-gray-800/50">
          <div className="flex items-center space-x-2">
            <div className={`w-2 h-2 rounded-full ${currentEnv.color}`}></div>
            <span className="text-sm text-gray-300">{currentEnv.text}</span>
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="ml-64 flex-1 flex flex-col bg-gray-50">
        {/* Header Component */}
        <Header currentPage={getCurrentPageTitle()} />

        {/* Page Content */}
        <main className="flex-1 p-8">
          {/* Page Title */}
          <div className="mb-8">
            <h1 className="text-2xl font-bold text-gray-900">{getCurrentPageTitle()}</h1>
            <p className="mt-1 text-sm text-gray-500">
              {getCurrentPageDescription()}
            </p>
          </div>

          {/* Main Content */}
          <div className="bg-white rounded-lg shadow-lg">
            {children}
          </div>
        </main>

        {/* Footer */}
        <footer className="bg-white border-t border-gray-200 p-4">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center text-sm text-gray-500">
              <p>Â© 2024 OpsCanvas. All rights reserved.</p>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Layout;
