// src/components/kubernetes/KubernetesDashboard.jsx
import React, { useState } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import ClusterOverview from './ClusterOverview';
import NamespaceManager from './NamespaceManager';
import WorkloadMonitoring from './WorkloadMonitoring';
import HelmChartManager from './HelmChartManager';
import RBACManager from './RBACManager';

const KubernetesDashboard = () => {
  const location = useLocation();
  const [activeView, setActiveView] = useState('clusters');

  const renderContent = () => {
    const path = location.pathname.split('/kubernetes/')[1] || 'clusters';
    
    switch (path) {
      case 'clusters':
        return <ClusterOverview />;
      case 'namespaces':
        return <NamespaceManager />;
      case 'workloads':
        return <WorkloadMonitoring />;
      case 'helm':
        return <HelmChartManager />;
      case 'rbac':
        return <RBACManager />;
      default:
        return <ClusterOverview />;
    }
  };

  return (
    <div className="p-6">
      {renderContent()}
    </div>
  );
};

export default KubernetesDashboard;
