import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Bell, Search, HelpCircle, ChevronDown, LogOut, UserCircle, Settings } from 'lucide-react';

const Header = () => {
  const navigate = useNavigate();
  const [showUserMenu, setShowUserMenu] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    navigate('/login');
  };

  return (
    <header className="h-14 bg-white border-b border-gray-200 flex items-center justify-between px-4">
      {/* Search */}
      <div className="flex-1 max-w-2xl">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
          <input
            type="text"
            placeholder="Search..."
            className="w-full pl-10 pr-4 py-1.5 text-xs bg-gray-50 border border-gray-200 rounded-md 
                     focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
      </div>

      {/* Right Side Actions */}
      <div className="flex items-center space-x-4">
        {/* Help */}
        <button className="p-1.5 text-gray-500 hover:bg-gray-100 rounded-md">
          <HelpCircle className="h-4 w-4" />
        </button>

        {/* Notifications */}
        <button className="p-1.5 text-gray-500 hover:bg-gray-100 rounded-md">
          <Bell className="h-4 w-4" />
        </button>

        {/* User Profile with Dropdown */}
        <div className="relative">
          <button 
            onClick={() => setShowUserMenu(!showUserMenu)}
            className="flex items-center space-x-2 hover:bg-gray-100 rounded-md p-1.5"
          >
            <div className="h-7 w-7 rounded-full bg-blue-500 flex items-center justify-center">
              <span className="text-xs font-medium text-white">A</span>
            </div>
            <div className="text-right">
              <div className="text-xs font-medium text-gray-900">Admin</div>
              <div className="text-[10px] text-gray-500">admin@cloudedge.dev</div>
            </div>
            <ChevronDown className="h-4 w-4 text-gray-500" />
          </button>

          {/* User Dropdown Menu */}
          {showUserMenu && (
            <div className="absolute right-0 mt-1 w-48 bg-white rounded-md shadow-lg border border-gray-200 py-1 z-50">
              <button 
                className="w-full flex items-center px-4 py-2 text-xs text-gray-700 hover:bg-gray-100"
                onClick={() => {/* Handle profile click */}}
              >
                <UserCircle className="h-4 w-4 mr-2" />
                Profile
              </button>
              
              <button 
                className="w-full flex items-center px-4 py-2 text-xs text-gray-700 hover:bg-gray-100"
                onClick={() => {/* Handle settings click */}}
              >
                <Settings className="h-4 w-4 mr-2" />
                Settings
              </button>
              
              <div className="border-t border-gray-200 my-1"></div>
              
              <button 
                className="w-full flex items-center px-4 py-2 text-xs text-red-600 hover:bg-red-50"
                onClick={handleLogout}
              >
                <LogOut className="h-4 w-4 mr-2" />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
